import { createAction } from "@reduxjs/toolkit";

export const onAddContact = createAction(
  "contact/add",
  ({ name, number, id }) => ({
    payload: {
      newContact: {
        name,
        number,
        id,
      },
    },
  }),
);

export const onRemoveContact = createAction("contact/remove");

export const onChangeFilter = createAction("contact/filter");

export const onSaveContact = createAction("contacts/save");

