import { createAction } from "@reduxjs/toolkit"
import actionTypes from "./contactsActionTypes"

const onAddContact = createAction("contact/add", ({ name, number, id }) => ({
  payload: {
    newContact: {
      name,
      number,
      id,
    },
  },
}))

const onRemoveContact = createAction('contact/remove');

